import axios from 'axios';
import Qs from 'qs';
import util from '@/libs/util';
Vue.use(D2Crud);
export default {
  data: function data() {
    return {
      columns: [{
        title: '日期',
        key: 'date',
        width: '180',
        sortable: true
      }, {
        title: '发布人',
        key: 'name',
        width: '180'
      }, {
        title: '内容',
        key: 'text',
        width: '1000'
      }],
      options: {
        stripe: true,
        highlightCurrentRow: true
      },
      data: [],
      addButton: {
        icon: 'el-icon-plus',
