      },
      rowHandle: {
        columnHeader: '编辑',
        edit: {
          icon: 'el-icon-edit',
          text: '编辑',
          size: 'small'
        },
        remove: {
          icon: 'el-icon-delete',
          size: 'small',
          fixed: 'right',
          confirm: true
        }
      },
      addTemplate: {
        id: {
          title: 'ID',
          value: 3,
          component: {
            span: 0
          }
        },
        date: {
          title: '日期',
          value: dayjs().format('YYYY-MM-DD'),
          component: {
            span: 12
          }
        },
        text: {
          title: '内容',
          value: '',
          component: {
            span: 24
          }
        },
        name: {
          title: '发布人',
          value: util.cookies.get("user-name"),
          component: {
            span: 12
          }
        }
      },
      addRules: {
        date: [{
