          message: '请输入日期',
          trigger: 'blur'
        }],
        text: [{
          required: true,
          message: '请输入内容',
          trigger: 'blur'
        }],
        name: [{
          required: true,
          message: '请输入发布人',
          trigger: 'blur'
        }]
      },
      formOptions: {
        labelWidth: '80px',
        labelPosition: 'left',
        saveLoading: false,
        gutter: 20
      },
      mylist: {}
    };
  },
  methods: {
    handleRowAdd: function handleRowAdd(row, done) {
      var _this2 = this;

      this.formOptions.saveLoading = true;
      setTimeout(function () {
        console.log(row);

        _this2.$message({
          message: '保存成功',
          type: 'success'
        }); //获取当前data长度


        var len = Object.keys(_this2.data).length; //写入row

        done({
          id: len + 1
        }); //将row压入data

        _this2.data.push(row);

        console.log(_this2.data); //调用函数

        _this2.sendNewRow(row);

        _this2.formOptions.saveLoading = false;
      }, 300);
    },
    handleRowEdit: function handleRowEdit(_ref, done) {
      var _this3 = this;

      var index = _ref.index,
          row = _ref.row;
      this.formOptions.saveLoading = true;
      setTimeout(function () {
        //console.log(index)
        //console.log(row)
        _this3.$message({
          message: '编辑成功',
          type: 'success'
        });

        done();

        _this3.alterMyRow(row);

        _this3.formOptions.saveLoading = false;
      }, 300);
    },
    handleRowRemove: function handleRowRemove(_ref2, done) {
      var _this4 = this;

      var index = _ref2.index,
          row = _ref2.row;
      setTimeout(function () {
        console.log(index);
        console.log(row);

        _this4.$message({
          message: '删除成功',
          type: 'success'
        });

        done();

        _this4.delMyRow(row.id);
      }, 300);
    },
    handleDialogCancel: function handleDialogCancel(done) {
      this.$message({
        message: '取消保存',
        type: 'warning'
      });
      done();
    },
    handleSelectionChange: function handleSelectionChange(selection) {
      console.log(selection);
    },
    getMyData: function getMyData() {
      var _this = this;

      axios.get('http://localhost:8080/GetAnn').then(function (response) {
        // console.log(response)
        _this.data = response.data; //console.log(Object.keys(_this.data).length)
      }).catch(function (error) {
        console.log(error);
      });
    },
    sendMyData: function sendMyData() {
      //获取处理过后的data
      var _this = this;

      axios.get('http://localhost:8080/RecAnn?data=' + this.data).then(function (response) {
        console.log(response);
      }).catch(function (error) {
        console.log(error);
      });
    },
    sendNewRow: function sendNewRow(newRow) {
      //添加新一行时，只发送新行的数据
      // console.log(newRow)
      axios.post('http://localhost:8080/AddAnnRow', Qs.stringify(newRow)).then(function (response) {
        console.log(response);
      }).catch(function (error) {
        console.log(error);
      });
    },
    alterMyRow: function alterMyRow(newRow) {
      //修改某一行，只发送当前行的数据
      console.log(newRow);
      axios.post('http://localhost:8080/AlterAnnRow', Qs.stringify(newRow)).then(function (response) {
        console.log(response);
      }).catch(function (error) {
        console.log(error);
      });
    },
    delMyRow: function delMyRow(ID) {
      //删除某一行，获取当前行的ID，
      axios.post('http://localhost:8080/DelAnnRow', Qs.stringify({
        id: ID
      })).then(function (response) {
        console.log(response);
      }).catch(function (error) {
        console.log(error);
      });
      console.log('删除了ID：' + ID);
    },
    addRow: function addRow() {
      this.$refs.d2Crud.showDialog({
        mode: 'add'
      });
    }
  },
  mounted: function mounted() {
    this.getMyData();
  },
  updated: function updated() {}
