import BasicContainer from '@vue-materials/basic-container';
import DeleteBalloon from './components/DeleteBalloon';
import EditDialog from './components/EditDialog';
import response from './tab-table.json';

export default {
  components: {
    BasicContainer,
    DeleteBalloon,
    EditDialog,
  },
  name: 'TabTable',

  data() {
    return {
      tabKey: 'all',
      dataSource: [],
      tabs: [
        { tab: '全部', key: 'all' },
        { tab: '待应答', key: 'inreview' },
        { tab: '处理中', key: 'released' },
        { tab: '已完成', key: 'rejected' },
      ],
      columns: [
        {
          title: '标题',
          dataIndex: 'title',
          key: 'title',
        },
        {
          title: '作者',
          dataIndex: 'author',
          key: 'author',
        },
        {
          title: '状态',
          dataIndex: 'status',
          key: 'status',
        },
        {
          title: '发布时间',
          dataIndex: 'date',
          key: 'date',
        },
        {
          title: '操作',
          key: 'action',
        },
      ],
      visible: false,
    };
  },

  created() {},

  mounted() {
    this.dataSource = response.data;
  },

  methods: {
    handleClick(tab) {
      console.log(tab);
    },
    handleRemove(index, tabKey) {
      this.dataSource[tabKey].splice(index, 1);
    },
    handleMod(row, index, tabKey) {
      this.$set(this.dataSource[tabKey], index, row);
    },
  },
}
